mapper.select:
WITH RNUMBUYER AS ( SELECT ROWNUM RNUM, A.* FROM ( SELECT BUYER_ID, BUYER_NAME, BUYER_LGU , BUYER_ADD1, BUYER_MAIL, BUYER_COMTEL , BUYER_CHARGER FROM BUYER
ORDER BY BUYER_LGU, BUYER_ID DESC ) A ) SELECT * FROM RNUMBUYER
WHERE RNUM >= #{startRow} AND RNUM <= #{endRow}

